window.onload=function(){var a=document.getElementsByClassName("scrollup")[0];window.onscroll=()=>{300<window.scrollY?a.classList.add("d-block"):a.classList.remove("d-block")},a.addEventListener("click",function(){window.scrollTo({top:0,behavior:"smooth"}),setTimeout(function(){history.pushState("",document.title,window.location.pathname+window.location.search)})})};